static inline void** C_PTR_ROOT_PUT (const u64 size) {

    assert_c_size(size);

    uint n = N_START;
    uint x = 0;

    // (1 << n) --> (2^n)
    // CONTINUA ANDANDO ENQUANTO PROVIDENCIARMOS TANTO
    while ((((1ULL << n) + ((1ULL << n) * x)/X_DIVISOR)) <= size)
        n += (x = (x + X_SALT) % X_LAST) == 0;

    // O ATUAL NÃO PROVIDENCIAMOS, ENTÃO RETIRA 1
    // voltasGrandes*(voltasMiniN) + voltasMini
    void** const ptr = BUFF_ROOTS + ((n - N_START)*(X_LAST/X_SALT) + x/X_SALT - 1);

    assert_root(ptr);

    return ptr;
}

static inline void** C_PTR_ROOT_GET (const u64 size) {

    assert_c_size(size);

    uint n = N_START; // TODO: FIXME: ambos devem ser uint mesmo?
    uint x = 0;

    // CONTINUA ANDANDO ENQUANTO O PROMETIDO NÃO SATISFAZER O PEDIDO
    while ((((1ULL << n) + ((1ULL << n) * x)/X_DIVISOR)) < size)
        n += (x = (x + X_SALT) % X_LAST) == 0;

    // voltasGrandes*(voltasMiniN) + voltasMini
    void** const ptr = BUFF_ROOTS + ((n - N_START)*(X_LAST/X_SALT) + x/X_SALT);

    assert_root(ptr);

    return ptr;
}

static inline void f_fill_and_register (void* const chunk, const u64 size) {

    assert_c_size(size);

    // FILL
    f_set_size (chunk, size);
    f_set_size2(chunk, size); void** const ptr = f_ptr_root_put(size);
    f_set_ptr  (chunk,  ptr);
    f_set_next (chunk, *ptr);

    // REGISTER
    if (*ptr)
        f_set_ptr(*ptr, f_get_next_(chunk));
    *ptr = chunk;

    // CONFIRMA QUE LÊ DE VOLTA A MESMA INFORMAÇÃO
    ASSERT(size == f_ld_size (chunk));
    ASSERT(size == f_ld_size2(chunk, size));
    //ASSERT(ptr  == f_ld_ptr  (chunk));
    //ASSERT(*ptr == f_ld_next (chunk));

    assert_f(chunk);
}

// TODO: FIXME: rename to unregister
static inline void f_remove (void* const chunk) {

    assert_c_is_f(chunk);

    if (f_ld_next(chunk)) {
        // TEM UM NEXT
        // O NEXT TAMBÉM TEM QUE SER UM FREE
        assert_c_is_f(f_ld_next(chunk));
        // O NEXT ASSUME O NOSSO PTR
        f_st_ptr(f_ld_next(chunk), f_ld_ptr(chunk));
    }

    // O PTR ASSUME O NEXT
    *f_ld_ptr(chunk) = f_ld_next(chunk);
}

